package com.bitsnbites.garagecai.Activity;import static android.content.Intent.FLAG_ACTIVITY_CLEAR_TASK;import android.content.Context;import android.content.Intent;import android.content.SharedPreferences;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.app.AppCompatDelegate;import com.bitsnbites.garagecai.R;import com.bitsnbites.garagecai.login.SignInActivity;import com.google.firebase.FirebaseApp;import com.google.firebase.auth.FirebaseAuth;import java.util.Timer;import java.util.TimerTask;public class Splash extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        SharedPreferences sharedPreferences = getSharedPreferences("Theme", Context.MODE_PRIVATE);        FirebaseApp.initializeApp(this);        setContentView(R.layout.activity_splash);        new Timer().schedule(new TimerTask() {            @Override            public void run() {                if (FirebaseAuth.getInstance().getCurrentUser() == null) {                    startActivity(new Intent(Splash.this, SignInActivity.class).setFlags(FLAG_ACTIVITY_CLEAR_TASK));                } else {                    startActivity(new Intent(Splash.this, MainActivity.class).setFlags(FLAG_ACTIVITY_CLEAR_TASK));                }            }        }, 2600);    }}