package com.bitsnbites.garagecai.login;import android.annotation.SuppressLint;import android.app.ProgressDialog;import android.content.Context;import android.content.Intent;import android.content.SharedPreferences;import android.net.Uri;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.app.AppCompatDelegate;import androidx.browser.customtabs.CustomTabsIntent;import com.bitsnbites.garagecai.R;public class SignInActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_sign_up);        EditText emailEt = findViewById(R.id.email);        EditText numberEt = findViewById(R.id.number);        Button next = findViewById(R.id.button);        TextView apply = findViewById(R.id.textView16);        next.setOnClickListener(view -> {            try {                String email, number;                email = emailEt.getText().toString();                number = numberEt.getText().toString();                if (email.length() > 5 && number.length() == 11) {                    checkCredential(email, number);                } else {                    emailEt.setError("Error mail");                    Toast.makeText(SignInActivity.this, "Error mail or Number", Toast.LENGTH_SHORT).show();                }            } catch (NullPointerException k) {                emailEt.setError("Error mail");                Toast.makeText(SignInActivity.this, "Error mail", Toast.LENGTH_SHORT).show();            }        });    }    private void checkCredential(String email, String number) {        ProgressDialog pg = new ProgressDialog(SignInActivity.this);        pg.setCancelable(false);        pg.setTitle("Checking credential... Please wait...");        pg.show();        Intent intent = new Intent(getApplicationContext(), VerifyActivity.class);        pg.dismiss();        intent.putExtra("user", "");        startActivity(intent);    }}