package com.bitsnbites.garagecai.login;import android.annotation.SuppressLint;import android.app.ProgressDialog;import android.content.Context;import android.content.Intent;import android.content.SharedPreferences;import android.net.Uri;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.app.AppCompatDelegate;import androidx.browser.customtabs.CustomTabsIntent;import com.bitsnbites.garagecai.R;public class SignInActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_sign_up);        EditText numberEt = findViewById(R.id.number);        Button next = findViewById(R.id.button);        TextView apply = findViewById(R.id.textView16);        next.setOnClickListener(view -> {                String number;                number = numberEt.getText().toString();                if(number.length()!=11){                    Toast.makeText(this, "Number must be 11 digit long", Toast.LENGTH_SHORT).show();                    return;                }            Intent intent = new Intent(getApplicationContext(), VerifyActivity.class);            intent.putExtra("number", number);            startActivity(intent);        });    }}